mapper.select:
		select * from EXCEL_CELL


                order by ${sidx} ${order}


                order by EXCEL_CELL_ID desc



			limit #{offset}, #{limit}

mapper.update:
update EXCEL_CELL
EXCEL_SHEET_ID = #{excelSheetId}, EXCEL_CELL_CN_NAME = #{excelCellCnName}, EXCEL_CELL_EN_NAME = #{excelCellEnName}, EXCEL_CELL_COL_NUM = #{excelCellColNum}, EXCEL_CELL_ROW_NUM = #{excelCellRowNum}, EXCEL_CELL_TYPE = #{excelCellType}, EXCEL_DATA_TYPE = #{excelDataType}, EXCEL_DEFAULT_VALUE = #{excelDefaultValue}, IS_DELETE = #{isDelete}
where EXCEL_CELL_ID = #{excelCellId}
