mapper.select:
SELECT AA.USR_ID,AA.USR_NAME, AA.ORG_TYPE, AA.USR_STA,AA.USR_NO,AA.ORG_NO,AA.ORG_NAME, AA.PASS_WORD,AA.LICENCE_CODE,AA.CERT_ID,AA.TM_SMP,AA.MBL_NO,AA.EMAIL,AA.BIRTH_DATE,AA.SEX,AA.SESSION_ID, WM_CONCAT(AA.ROLE_NAME) AS ROLE_NAME ,WM_CONCAT(AA.ROLE_ID) AS ROLE_ID FROM ( SELECT DISTINCT U .*, ORG.ORG_NAME, R.ROLE_NAME,R.ROLE_ID FROM USR_INFO U JOIN ORG_INFO ORG ON U .ORG_NO = ORG.ORG_NO LEFT JOIN USR_ROLE UR ON U .USR_ID = UR.USR_ID LEFT JOIN ROLE R ON R.ROLE_ID = UR.ROLE_ID WHERE 1=1
and u.usr_sta=#{usrSta,jdbcType=VARCHAR}
and u.usr_no=#{usrNo,jdbcType=VARCHAR}
and u.usr_name like CONCAT(CONCAT('%',#{usrName}),'%')
and u.usr_id like CONCAT(CONCAT('%',#{usrId}),'%')
and u.login_name = #{loginName,jdbcType=VARCHAR}
and u.PASS_WORD = #{passWord,jdbcType=VARCHAR}
and u.ORG_TYPE IN ${orgType}
ORDER BY U .USR_ID DESC ) AA GROUP BY AA.USR_ID, AA.USR_NAME, AA.ORG_TYPE, AA.USR_STA,AA.PASS_WORD,AA.LICENCE_CODE,AA.CERT_ID,AA.TM_SMP,AA.MBL_NO,AA.EMAIL,AA.BIRTH_DATE,AA.SEX,AA.SESSION_ID,AA.ORG_NO,AA.ORG_NAME,AA.USR_NO ORDER BY AA.ORG_TYPE,AA.USR_ID DESC

  	SELECT U.*

	FROM  USR_INFO U
    WHERE 1=1

		and u.login_name=#{loginName,jdbcType=VARCHAR}


		and u.login_name !=#{oldLoginName,jdbcType=VARCHAR}

  	SELECT T .usr_id, T .usr_name,T.USR_STA, T .TM_SMP, T .email, T .mbl_no, ur.role_id, r.role_name
	FROM usr_info T
	JOIN AQ_BASE_TELLERS_REL b ON T .usr_id = b.user_id
	LEFT JOIN usr_role ur ON T .usr_id = ur.usr_id
	LEFT JOIN role r ON r.role_id = ur.role_id
	WHERE b.pk_id = #{0}
	AND b.pk_table = #{1}

  		and T.USR_NAME = #{user.usrName,jdbcType=VARCHAR}

	ORDER BY T .TM_SMP

mapper.update:
update USR_INFO
set USR_STA = '0'
where USR_ID = #{usrId,jdbcType=DECIMAL}

update USR_INFO
set USR_STA = '2'
where USR_ID = #{usrId,jdbcType=DECIMAL}
